class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0;
        List<Long> list = new ArrayList<>();
        boolean pos = true;

        for(int i = 0; i < nums.length; i++){
            long gg = (long)nums[i] - (long)target[i];

            if(gg > 0 && pos){
                list.add(gg);
            }
            else if(gg < 0 && !pos){
                list.add(gg);
            }
            else {
                // process
                //System.out.println("process: " + list);
                Stack<Long> stack = new Stack<>();
                stack.push(0L);
                for(int j = 0; j < list.size(); j++){
                    long cur = Math.abs(list.get(j));
                    boolean ok = false;
                    while(stack.peek() > cur){
                        ok = true;
                        stack.pop();
                    }
                    if(!ok) ans += cur - stack.peek();
                    stack.push(cur);
                }
                //System.out.println("result ans = " + ans);

                //
                list = new ArrayList<>();
                list.add(gg);
                if(gg > 0){
                    pos = true;
                }
                else if(gg < 0){
                    pos = false;
                }
            }
        }
        Stack<Long> stack = new Stack<>();
        stack.push(0L);
       // System.out.println("process: " + list);
        for(int j = 0; j < list.size(); j++){
            long cur = Math.abs(list.get(j));
            boolean ok = false;
            while(stack.peek() > cur){
                ok = true;
                stack.pop();
            }
            if(!ok) ans += cur - stack.peek();
            stack.push(cur);
        }


        return ans;

    }
}
