class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long cnt = 0;
        int n = nums.size();
        int pre = 0;
        for(int i=0;i<n;i++){
            int t = target[i]-nums[i];
            if(pre==0){
                cnt+=abs(nums[i]-target[i]);
                pre = target[i]-nums[i];
            }
            else if(t==0){
                pre = 0;
            }
            else if(t>0 && pre>0){
                if(t>pre)cnt+=t-pre;
            }
            else if(t<0 && pre<0){
                if(abs(t)>abs(pre))cnt+=abs(t-pre);
            }
            else{
                cnt+=abs(t);
            }
            pre = t;
        }
        return cnt;
    }
};
