class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int cur = 0;
        long res = 0;
        for (int i = 0; i < nums.size(); i++) {
            int t = target[i] - nums[i];
            if (cur <= t) {
                res += max(0, t - max(0, cur));
            }
            else {
                res += max(0, min(0, cur) - t);
            }
            cur = t;
        }
        return res;
    }
};
