typedef long long LL;

class Solution {
public:
    long long minimumOperations(vector<int>& b, vector<int>& c) {
        int n = b.size();
        int a[n + 1];
        memset(a, 0, sizeof a);
        for (int i = 1; i <= n; i ++) {
            a[i] = c[i - 1] - b[i - 1];
        }

        LL s[n + 1];
        memset(s, 0, sizeof s);
        for (int i = 1; i <= n; i ++)  s[i] = a[i] - a[i - 1]; 

        LL p = 0, q = 0;
        for (int i = 1; i <= n; i ++)
        {
            if(s[i] > 0)    p += s[i];
            else    q -= s[i]; 
        }
        // cout << p << " " << q << endl; 

        return max(p, q);
    }
};
