class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        target = [b-a for a,b in zip(nums,target)]
        # 差分数组?
        target = [0]+target+[0]
        diff = [b-a for b,a in zip(target[1:],target)]
        return sum(a for a in diff if a >0)
