class Solution(object):
    def minimumOperations(self, nums, target):
        """
        :type nums: List[int]
        :type target: List[int]
        :rtype: int
        """
        diff = [nums[i] - target[i] for i in range(len(nums))]

        prev_pos = 0
        prev_neg = 0
        ans = 0

        for d in diff:
            if d >= 0:
                if d > prev_pos:
                    ans += d - prev_pos
                prev_pos = d
                prev_neg = 0  # Resetting prev_neg
            else:
                if -d > prev_neg:
                    ans += -d - prev_neg
                prev_neg = -d
                prev_pos = 0  # Resetting prev_pos

        return ans
