class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for(int i = 0; i < target.size(); i++) nums[i] = target[i] - nums[i];
        long long tmp = abs(nums[0]), ans = abs(nums[0]);
        for(int i = 1; i < nums.size(); i++){
            if(((nums[i] > 0) && (nums[i-1] > 0)) || ((nums[i] <= 0) && (nums[i-1] <= 0))){
                if(abs(nums[i]) > tmp){
                    ans += abs(nums[i]) - tmp;
                    tmp = abs(nums[i]);
                }
                else tmp = abs(nums[i]);
            }
            else{
                ans += abs(nums[i]);
                tmp = abs(nums[i]);
            }
        }
        return ans;
    }
};
