class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        long long int result = 0;

        vector<int> temp;

        for(int i=0;i<target.size();i++)
            temp.push_back(nums[i]-target[i]);

        result = abs(temp[0]);

        for(int i=1;i<temp.size();i++)
        {
            if(temp[i] == 0)
                continue;

            if(temp[i] > 0)
            {
                if(temp[i-1] >= 0)
                {
                    if(temp[i] > temp[i-1])
                    result += temp[i] - temp[i-1];
                }
                else
                {
                    result += temp[i];
                }
            }
            else
            {
                if(temp[i-1] >= 0)
                {
                    result += abs(temp[i]);
                }
                else
                {
                    if(temp[i] < temp[i-1])
                    result += abs(temp[i] - temp[i-1]);
                }
            }
        }

        return result;
    }
};
