class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long[] d = new long[n+2];
        long ans = 0;
        for(int i = 0; i < n; i++) d[i+1] = nums[i] - target[i];
        for (int i = 0; i <= n; ++i)
            ans += Math.abs(d[i] - d[i + 1]);
        return ans/2l;
    }
}
