class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        N = len(nums)
        res = 0
        carryover = 0

        for i in range(N):
            diff = target[i] - nums[i]

            if diff > 0:
                if carryover > 0:
                    if diff >= carryover:
                        res += diff - carryover
                else:
                    res += diff
            else:
                if carryover < 0:
                    if diff <= carryover:
                        res -= diff - carryover
                else:
                    res -= diff

            carryover = diff

        return res
