class Solution {
public:
    long long b1[100010],b2[100010];
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size(),m = target.size();
        for(int i = 0;i < n;i ++)
        {
            if(i == 0)
            b1[i + 1] = nums[i];
            else
            b1[i + 1] = nums[i] - nums[i - 1];
        }
        for(int i = 0;i < m;i ++)
        {
            if(i == 0)
            b2[i + 1] = target[i];
            else
            b2[i + 1] = target[i] - target[i - 1];
        }
        for(int i = 1;i <= n;i ++)
        {
            b1[i] = b1[i] - b2[i];
        }
        long long x = 0,y = 0;
        for(int i = 1;i <= n;i ++)
        {
            if(b1[i] >= 0)
                x += b1[i];
            else
                y -= b1[i];
        }
        return max(x,y);
    }
};
