class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        long long ret = 0;
        int n = a.size();
        int pre = 0;
        for(int i = 0;i<n;i++){
            int diff = b[i] - a[i];
            if(diff>=0){
                if(pre>=0){
                    if(diff>=pre){
                        ret += (diff-pre);
                    }
                } else {
                    ret += diff;
                }
            } else {
                if(pre>=0){
                    ret -= diff;
                } else {
                    if(diff < pre) {
                        ret += (pre-diff);
                    }
                }
            }
            pre = diff;
        }
        return ret;
    }
};
