class Solution(object):
    def minimumOperations(self, nums, target):
        """
        :type nums: List[int]
        :type target: List[int]
        :rtype: int
        """
        n = len(nums)
        dif = nums[0] - target[0]
        ans = abs(dif)
        for i in range(1, n):
            d = nums[i] - target[i]
            if d == 0:
                dif = 0
                continue
            if dif == 0:
                ans += abs(d)
                dif = d
                continue
            else:
                if d * dif > 0:
                    if d > 0:
                        if d >= dif: 
                            ans += d - dif
                            dif = d
                        else:
                            dif = d
                    else:
                        if d >= dif:
                            dif = d
                        else:
                            ans += dif - d
                            dif = d
                else:
                    ans += abs(d)
                    dif = d
        return ans
