#define ll long long

class Solution {
public:

    bool isDifferent(ll a, ll b) {
        return (a <= 0 && b >= 0) || (a >= 0 && b <= 0);
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans = 0;
        ll n = nums.size();

        vector<ll> diff(n);

        for(int i = 0; i<n; i++) {
            diff[i]  = target[i] - nums[i];
        }

        ll left = 0;
        ll right = 1;
        ll minus = 0;

        ans = abs(diff[left]);
        minus = ans;

        while(right < n) {
            if(isDifferent(diff[left], diff[right])) {
                left = right;
                right++;
                ans += abs(diff[left]);
                minus = abs(diff[left]);
                continue;
            }

            if(diff[left] <= 0) {
                ll mn = abs(diff[right]);
                diff[right] += minus;
                diff[right] = min(diff[right], 0ll);
                ans += (ll) abs(diff[right]);
                minus = mn;
                right++;
            }   

            else{
                ll mn = abs(diff[right]);
                diff[right] -= minus;
                diff[right] = max(diff[right], 0ll);
                ans += (ll)diff[right];
                minus = mn;
                right++;
            }
        }

        return ans;
    }

};
