typedef long long ll;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        ll n=nums.size();
        vector<ll> v(n);
        ll i;
        for(i=0;i<n;i++){
            v[i]=nums[i]-t[i];
        }
        ll ans=0,pre=0;
        for(i=0;i<n;i++){
            if(v[i]>0){
                ans+=max((ll)0,pre-v[i]);
                pre=v[i];
            }
            else{
                ans+=pre;
                pre=0;
            }
        }
        ans+=pre;
        pre=0;
        for(i=0;i<n;i++){
            if(v[i]<0){
                ans+=max((ll)0,pre-abs(v[i]));
                pre=abs(v[i]);
            }
            else{
                ans+=pre;
                pre=0;
            }
        }
        ans+=pre;
        return ans;
    }
};
