class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [num - t for num, t in zip(nums, target)]
        n = len(nums)
        ans = 0
        lst = 0
        mm = 0
        stack = []
        for i, di in enumerate(diff):
            if not stack:
                stack.append(di)
                ans += abs(di)
            elif stack[-1] * di > 0:
                if abs(stack[-1]) < abs(di):
                    ans += abs(di) - abs(stack[-1])
                while stack and abs(stack[-1]) > abs(di):
                    stack.pop()

                stack.append(di)
            else:
                ans += abs(di)
                stack = [di]

        return ans
