class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n=nums.size();
        vector<long long >diff(n+1,0);
        for(int i=0;i<n;i++)
        {
            diff[i+1]=target[i]-nums[i];
        }
        long long ans=0;
        auto get=[&](vector<int>vec){
            for(auto &x:vec)x=abs(x);
            long long ans=vec[0];
            for(int i=1;i<vec.size();i++){
                if(vec[i]<=vec[i-1]){
                  continue;
                }else{
                    ans+=vec[i]-vec[i-1];
                }
            }
            return ans;
        };
        for(int i=1;i<=n;i++){
           if(diff[i]==0)continue;
           vector<int>vec;
           int j=i;
           while(j<=n and diff[j]*diff[i]>0){
               vec.push_back(diff[j]);
               j++;
           }
           ans+=get(vec);
           i=j-1;
        }
        return ans;
    }
};
