using ll = long long ;
using vi = vector<ll>;
using vvi = vector<vi>;

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vi c(n);
        for (int i=0; i<n; i++) {
            c[i] = b[i] - a[i];
        }
        vvi d;
        for (auto x : c) {
            if (x == 0) d.push_back({});
            else if (d.empty() || d.back().empty()) d.push_back({x});
            else {
                int y = d.back().back();
                if (x * y < 0) d.push_back({x});
                else d.back().push_back(x);
            }
        }
        ll ans = 0;
        for (auto &v : d) {
            if (v.empty()) continue;
            auto f = [&] (vi &a) {
                int n = a.size();
                int ans = 0, last = 0;
                for (auto x : a) {
                    x = abs(x);
                    ans += max(0ll,x-last);
                    last = x;
                }
                return ans;
            };
            ans += f(v);
        }
        return ans;

    }
};
