/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
    const N = nums.length
    const diffs = nums.map((num, i) => target[i] - num)
    let ans = 0
    for (let i = 0, acc = 0; i < N; i++) {
        const diff = diffs[i]
        if (diff * acc >= 0) {
            ans += Math.max(0, Math.abs(diff) - Math.abs(acc))
        } else {
            ans += Math.abs(diff)
        }
        acc = diff
    }
    return ans
};
