class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
        }
        long long ans=abs(diff[0]);
        int flag=diff[0]<0?-1:1;
        for(int i=1;i<n;i++){
            if(flag==1){
                if(diff[i]>0){
                    if(diff[i]>diff[i-1]){
                        ans+=diff[i]-diff[i-1];
                    }
                }
                else{
                    flag=-1;
                    ans-=diff[i];
                }
            }
            else{
                if(diff[i]<0){
                    if(diff[i]<diff[i-1]){
                        ans+=diff[i-1]-diff[i];
                    }
                }
                else{
                    flag=1;
                    ans+=diff[i];
                }
            }
        }      
        return ans;
    }
};
