class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        curr = 0
        x = 0
        lst = [target[i] - nums[i] for i in range(len(nums))]
        print(lst)
        for i in lst:
            if abs(i) != i and abs(curr) != curr:
                if abs(i) > abs(curr):
                    x += abs(i) - abs(curr)
            elif abs(i) == i and abs(curr) == curr:
                if i > curr:
                    x += i - curr

            else:
                x += abs(i)
            curr = i
        return x
