using ll = long long;
class Solution {
public:
  long long minimumOperations(vector<int>& a, vector<int>& b) {
    ll ans = 0; int l = a.size();
    for(int cur = 0, i =0; i<l;++i) {
      int ab = -a[i] + b[i];
      if(ab ==0 ) {
        cur = 0; continue;
      }
      if(ab < 0 && cur>0) cur = 0;
      if(ab > 0 && cur<0) cur = 0;
      ans+=max(0, abs(ab) - abs(cur));
      cur = ab;
    }
    return ans;
  }
};
