class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& v) {
        int i, n = a.size(), e = 0, d;
        long long ans = 0;
        for (i = 0; i < n; i++) {
            if (a[i] + e == v[i])
                continue;
            d = v[i] - a[i];
            if (e >= 0) {
                if (d > e)
                    ans += d - e;
                else if (d < 0)
                    ans -= d;
            } else if (e < 0) {
                if (d < e)
                    ans += e - d;
                else if (d > 0)
                    ans += d;
            }
            e = d;
            // cout << i << ' ' << e << ' ' << ans << endl;
        }
        return ans;
    }
};
