class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int>diff(n);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
        }
       long long int sum=0;
        int i=0;
        int j=0;
        while(i<n && j<n){
            if(diff[i]>0){int prev=0;
               while(j<n && diff[j]>0){
                int val=diff[j]-prev;
                int max1=max(val,0);
                sum+=max1;
                prev=diff[j];
                j++;
               }
               i=j;
               i--;
               j--;
            }else if(diff[i]==0){
                while(j<n && diff[j]==0){
                    j++;
                }
                i=j;
                i--;
                j--;
            }else{int prev=0;
                while(j<n && diff[j]<0){
                    int val=abs(diff[j])-abs(prev);
                    int max1=max(val,0);
                    sum+=max1;
                    prev=abs(diff[j]);
                    j++;
                }
                i=j;
                i--;
                j--;
            }
            i++;
            j++;
        }
        return sum;
    }
};
