class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n=target.length;
        for(int i=0;i<n;i++) nums[i]-=target[i];
        int f=nums[0];
        long ans=Math.abs(f);
        for(int i=1;i<n;i++){
            if((nums[i]>0 && f>0)|| (nums[i]<0 && f<0)){
                if(Math.abs(f)>=Math.abs(nums[i])){
                    f=nums[i];

                    }
                else{
                   ans+=Math.abs(nums[i]-f);
                    f=nums[i];
                    }
                }
            else{
                f=nums[i];
                ans+=Math.abs(f);
                }
            }
        return ans;

    }
}
