class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long int ans = 0;
        ans += abs(nums[0] - target[0]);
        for(int i = 1; i < n; i++){
            int curr = nums[i] - target[i];
            int prev = nums[i-1] - target[i-1];
            if(curr == prev){
                continue;
            }
            else if(prev >= 0 && curr > prev){
                ans += curr - prev;
            }
            else if(prev <= 0 && curr < prev){
                ans += prev - curr;
            }
            else if(curr > 0 && prev < 0){
                ans += curr;
            }
            else if(prev > 0 && curr < 0){
                ans -= curr;
            }
        }
        return ans;
    }
};
