class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int ans=0;
        vector<int> v;
        for(int i=0;i<nums.size();i++)
        {
            v.push_back(target[i]-nums[i]);
        }
        for(int i=0;i<v.size();i++)
        {
            vector<int> v1;
            if(v[i]>=0)
            {
                while(i<v.size() && v[i]>=0)
                {
                    v1.push_back(v[i]);
                    i++;
                }
                ans+=v1[0];
                for(int j=1;j<v1.size();j++)
                {
                    ans+=max(v1[j]-v1[j-1],0);
                }
                i--;
            }
            else
            {
                while(i<v.size() && v[i]<0)
                {
                    v1.push_back(v[i]);
                    i++;
                }
                ans-=v1[0];
                for(int j=1;j<v1.size();j++)
                {
                    ans+=max(v1[j-1]-v1[j],0);
                }
                i--;
            }
        }
        return ans;
    }
};
