class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll da=0,db=0;
		vector<int> a,b;
		int n=nums.size();
		a.resize(n);
		b.resize(n);
		a[0]=nums[0]; b[0]=target[0];
		rep(i,1,n-1) {
			a[i]=nums[i]-nums[i-1];
			b[i]=target[i]-target[i-1];
		}
		rep(i,0,n-1) {
			if (a[i]>=b[i]) da+=a[i]-b[i];
			else db+=b[i]-a[i];
		}
		ll ans=max(da,db);
		return ans;
    }
};
