class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n, 0);
        for(int i = 0; i < n; i++){
            diff[i] = target[i] - nums[i];
        }
        long long res = 0;
        int prev = 0;
        for(int i = 0; i < n; i++){
            if(diff[i] == 0){
                prev = diff[i];
                continue;
            }
            if(diff[i] > 0){
                if(prev < 0){
                    res += diff[i];
                }
                else{
                    if(diff[i] > prev){
                        res += diff[i] - prev;
                    }
                }
            }
            else{
                if(prev > 0){
                    res += abs(diff[i]);
                }
                else{
                    if(diff[i] < prev){
                        res += abs(diff[i] - prev);
                    }
                }
            }
            prev = diff[i];
        }
        return res;
    }
};
