class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        A = [b - a for a,b in zip(nums, target)]
        res = abs(A[0])
        for i in range(1, n):
            if 0 <= A[i - 1] <= A[i]:
                res += A[i] - A[i - 1]
            elif 0 >= A[i - 1] >= A[i]:
                res += A[i - 1] - A[i]
            elif abs(A[i - 1] - A[i]) == abs(A[i]) + abs(A[i - 1]):
                res += abs(A[i])
        return res
