class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i=0; i<n; i++) {
            nums[i] -= target[i];
        }

        int prev = nums[0];
        long long ans = abs(nums[0]);
        for (int i=1; i<n; i++) {
            if ((prev < 0 && nums[i] > 0) || (prev > 0 && nums[i] < 0) || nums[i] == 0) {
                prev = nums[i];
                ans += abs(nums[i]);
            } else {
                if (nums[i] < 0) {
                    ans += abs(nums[i] - max(nums[i], prev));
                } else {
                    ans += abs(nums[i] - min(nums[i], prev));
                }
                prev = nums[i];
            }
        }
        return ans;
    }
};
