class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        common = 0
        result = 0
        diff = [t-n for t,n in zip(target, nums)]
        for i in range(len(nums)):
            if diff[i]*common <= -1:
                result += abs(diff[i])
                common = diff[i]
            else:
                if abs(common) >= abs(diff[i]):
                    common = diff[i]
                else:
                    result += abs(diff[i]) - abs(common)
                    common = diff[i]
        return result
