class Solution {
public:
#define ll long long
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans=0, n=nums.size();
        vector<ll> dp(n,0);
        dp[0]=target[0]-nums[0];
        ans=abs(dp[0]);
        for(int i=1;i<n;i++){
            dp[i]=target[i]-nums[i];
            if(dp[i]>0){
                if(dp[i-1]>0) ans+=max(0ll,dp[i]-dp[i-1]);
                else ans+=dp[i];
            }
            else if (dp[i]<0){
                if(dp[i-1]<0) ans+=max(0ll,dp[i-1]-dp[i]);
                else ans+=abs(dp[i]);
            }
        }
        return ans;
    }
};
