class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0, res = 0, front = 0;
        int n = nums.size();

        for(int i = 0; i < n; i++)
        {
            int num = nums[i], temp = target[i];
            nums[i] = temp - nums[i] - front;
            front = temp - num;
            if(nums[i] > 0) ans += nums[i];
            else if(nums[i] < 0) res -= nums[i];
        }

        return ans > res ? ans : res;
    }
};
