class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        for(int i=0;i<n;i++){
            nums[i]=nums[i]-target[i];
        }
        int change=0;
        for(int i=0;i<n;i++){
            if(nums[i]==0){
                change=0;
            }
            else if(nums[i]>0){
                // needs to decrement
                if(change>0) change=0;

                if(change<-nums[i]){
                    change=-nums[i];
                }
                else{
                    ans=ans+(nums[i]+change);
                    change=-nums[i];
                }
            }
            else{
                // needs to incre
                // nums[i]<0
                int req=abs(nums[i]);
                if(change<0) change=0;
                if(change>req){
                    change=req;
                } else{
                    ans+=(req-change);
                    change=req;
                }
            }
        }
        return ans;
    }
};
