class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        diff = [j-i for i,j in zip(nums, target)]


        res = 0
        prev = 0
        for v in diff:
            # print(v, prev)
            if prev == v:
                pass
            else:
                if abs(prev) > abs(v) and prev * v >0:
                    prev = v
                if prev * v <= 0:
                    prev = 0
                res += abs(v-prev)
                prev = v
            # print(res)
        return res
