class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long det[n];
        for (int i = 0; i < n; i++) det[i] = target[i] - nums[i];

        auto sgn = [&](long long x) {
            if (x == 0) return 0;
            if (x > 0) return 1;
            return -1;
        };

        auto gao = [&](int l, int r) {
            long long ret = 0, last = 0;
            for (int i = l; i <= r; i++) {
                long long x = abs(det[i]);
                if (x > last) ret += x - last;
                last = x;
            }
            return ret;
        };

        long long ans = 0;
        for (int i = 0, j = 0; i < n; i++) {
            if (i == n - 1 || sgn(det[i + 1]) != sgn(det[j])) {
                ans += gao(j, i);
                j = i + 1;
            }
        }
        return ans;
    }
};
