class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diffs(nums.size());
        for (int i = 0; i < nums.size(); i++) {
            diffs[i] = nums[i] - target[i];
        }

        if (diffs.size() == 1) {
            return abs(diffs[0]);
        }

        for (auto x : diffs) {
            cout << x << ' ';
        }

        long long s = abs(diffs[0]);
        for (int i = 1; i < diffs.size(); i++) {
            if (diffs[i] >= diffs[i-1] && diffs[i-1] >= 0) {
                s += diffs[i] - diffs[i-1];
                continue;
            }
            if (diffs[i] <= diffs[i-1] && diffs[i-1] <= 0) {
                s += -(diffs[i] - diffs[i-1]);
                continue;
            }
            if (diffs[i] > 0 &&  diffs[i-1] < 0)
                s += abs(diffs[i]);
            if (diffs[i] < 0 &&  diffs[i-1] > 0)
                s += abs(diffs[i]);
        }
        return s;

    }
};
