class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        int m = target.size();

        // 确保两个数组大小相同
        if (n != m) {
            return -1; // 或者根据具体需求处理
        }

        // 计算差分数组 diff_nums 和 diff_target
        vector<long long> diff_nums(n);
        vector<long long> diff_target(n);
        diff_nums[0] = nums[0];
        diff_target[0] = target[0];
        for (int i = 1; i < n; ++i) {
            diff_nums[i] = nums[i] - nums[i-1];
            diff_target[i] = target[i] - target[i-1];
        }

        // 计算差值数组 diff
        vector<long long> diff(n);
        for (int i = 0; i < n; ++i) {
            diff[i] = diff_target[i] - diff_nums[i];
        }

        // 累加正差值和负差值
        long long positive_sum = 0;
        long long negative_sum = 0;
        for (int i = 0; i < n; ++i) {
            if (diff[i] > 0) {
                positive_sum += diff[i];
            } else {
                negative_sum -= diff[i]; // 注意这里是减去负值，相当于加上绝对值
            }
        }

        // 最终的操作次数是正差值和负差值中的较大值
        return max(positive_sum, negative_sum);
    }
};
