class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        q = 0
        d = [0]
        neg = True
        res = 0
        for a,b in zip(nums, target):
            if a-b > 0 and neg:
                d.append(0)
                neg = False
            elif a-b < 0 and not neg:
                d.append(0)
                neg = True
            d.append(abs(a-b))
        d.append(0)
        for i in d:
            if i >= q:
                q = i
            else:
                res += q-i
                q = i
        return res
