class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [0] * n
        diff[0] = target[0] - nums[0]
        res = abs(target[0] - nums[0])

        for i in range(1, n):
            gap = target[i] - nums[i]
            diff[i] = gap
            if (gap >=0 and diff[i-1] >= 0) or (gap <= 0 and diff[i-1] <= 0):
                res += max(0, abs(gap) - abs(diff[i-1]))
            else:
                res += abs(gap)
        return res
