class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long int> v(target.size());
        int n=target.size();
        for(int i=0;i<n;i++)
            v[i]=target[i]-nums[i];
        vector<long long int> diff(n);
        diff[0]=v[0];
        for(int i=1;i<n;i++)
            diff[i]=v[i]-v[i-1];
        long long int ans1=0ll,ans2=0ll;
        for(int i=0;i<n;i++)
        {
            // if(i==0)
            //     ans+=(v[i]);
            // else 
            //     ans+=(v[i]-v[i-1]);
            if(diff[i]>0)
            ans1+=abs(diff[i]);
            else
                ans2+=(abs(diff[i]));
        }
        long long int ans=max(ans1,ans2);
        return ans;
    }
};
