class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long inc = 0, dec = 0, ans = 0;

        int n = nums.size();
        for(int i = 0; i < n; i++) {
            if(target[i] == nums[i]) {
                inc = min(inc, dec);
                dec = inc;
            } else if(target[i] > nums[i]) {
                long long req = target[i] - nums[i];

                if(req > inc) {
                    dec = 0;
                    long long needed = req - inc;
                    inc += needed;
                    ans += needed;
                } else {
                    long long extra = inc - req;
                    dec = min(dec, extra);
                    inc = req + dec;
                }
            } else {
                long long req = nums[i] - target[i];

                if(req > dec) {
                    inc = 0;
                    long long needed = req - dec;
                    dec += needed;
                    ans += needed;
                } else {
                    long long extra = dec - req;
                    inc = min(inc, extra);
                    dec = req + inc;
                }
            }

            // cout << ans << "\n";
        }

        return ans;
    }
};
