#define _int64 long long

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int i,j,n;
        _int64 ans,now,tmp;
        ans=0;
        now=0;
        n=a.size();
        for (i=0;i<n;i++)
        {
            tmp=a[i]-b[i];
            if (tmp>now) ans+=tmp-now;
            now=tmp;
        }
        if (now<0) ans+=0-now;
        return ans;
    }
};
