class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        long long cur=0;
        for(int i=0;i<n;i++){
            long long d=target[i]-nums[i];
            if((d>0&&cur>0)||(d<0&&cur<0)){
                if(abs(d)>abs(cur)){
                    ans+=abs(d)-abs(cur);
                }
            }else{
                ans+=abs(d);
            }
            cur=d;
        }
        return ans;
    }
};
