class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:


        diff = []
        for i in range(len(nums)):
            diff.append(nums[i] - target[i])

        p = []
        n = []
        for d in diff:
            if d >= 0:
                p.append(d)
                n.append(0)
            else:
                p.append(0)
                n.append(-d)

        prev = 0
        ans = 0
        for pd in p:
            if pd > prev:
                ans += pd-prev
            prev = pd

        prev = 0
        for nd in n:
            if nd > prev:
                ans += nd-prev
            prev = nd

        return ans
