class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        for(int i=0;i<nums.size();i++)
        {
            diff.push_back(target[i]-nums[i]);
        }

        long long int ans=abs(diff[0]);
        for(int i=1;i<nums.size();i++)
        {
            if(diff[i]>=0 && diff[i-1]>=0)
            {
                ans=ans+max(diff[i]-diff[i-1], 0);
            }
            else if(diff[i]<=0 && diff[i-1]<=0)
            {
                ans=ans+max(diff[i-1]-diff[i], 0);
            }
            else
            {
                ans=ans+abs(diff[i]);
            }
        }

        return ans;
    }
};
