class Solution:
    def minNumberOperations(self, target: List[int]) -> int:
        n = len(target)

        if n == 0:
            return 0

        if target[0] < 0:
            for i in range(n):
                target[i] = -target[i]

        ans = target[0]
        for i in range(1, n):
            ans += max(target[i] - target[i - 1], 0)
        return ans

    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        lst = [a - b for a, b in zip(nums, target)]

        ans = 0
        pre = []
        for num in lst:
            if num == 0:
                ans += self.minNumberOperations(pre)
                pre = []
            elif num > 0:
                if not pre or pre[-1] > 0:
                    pre.append(num)
                else:
                    ans += self.minNumberOperations(pre)
                    pre = [num]
            else:
                if not pre or pre[-1] < 0:
                    pre.append(num)
                else:
                    ans += self.minNumberOperations(pre)
                    pre = [num]
        ans += self.minNumberOperations(pre)
        return ans
