class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int a[100005];
        for(int i=0;i<nums.size();i++) a[i]=nums[i] - target[i];

        long long ans = 0;
        int pre = 0;
        for(int i=0;i<nums.size();i++) {
            if(a[i] > 0) {
                if(pre >= 0) {
                    ans += max(0, a[i] - pre);
                }
                else {
                    ans += a[i];
                }
            } else {
                if(pre <= 0) {
                    ans += max(0, pre - a[i]);
                }
                else {
                    ans += -a[i];
                }
            }

            pre = a[i];
        }

        return ans;
    }
};
