class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long add=0,mis=0,ans=0;
        for(int i=0;i<nums.size();i++){
            long long d=target[i]-nums[i];
            if(d>0){
                mis=0;
                ans+=max(0LL,d-add);
                add=d;                
            }else{
                add=0;
                ans+=max(0LL,-d-mis);
                mis=-d;
            }
        }      
        return ans;
    }
};
